function [gid, Y, X] = partition(n, N)   %n =1000; p = 10; sigma = 0.001;%  [Y, X] = gen_synthetic_data(n, p, sigma)%  disp(Y(2,:))    load X.dat  load Y.dat  nd = n / N; % # of data in one partition  gid = zeros(n,1); % # of partition one data point is in  countP = zeros(N, 1) % # of data in each partition  for i = 1:n    gid(i) = ceil(rand(1)*N)    while countP(gid(i,1)) == nd      gid(i,1) = ceil(rand(1)*N)    end    countP(gid(i,1),1) = countP(gid(i,1), 1) + 1  end  save pid.dat gid -asciiend