function [thetaih, thetaho] = InitWeight(din, dhid, dout)    boundih = sqrt(6/(din + dhid)) % the upper bound of weight initialization between input and hidden layers    boundho = sqrt(6/(dhid + dout)) % the upper bound of weight initialization between hidden and output layers        thetaih = rand(din, dhid) * 2 * boundih - boundih    thetaho = rand(dhid, dout) * boundho - boundho % initialize weights        thetaih = [zeros(1, dhid); thetaih]    thetaho = [zeros(1, dout); thetaho] % add weights for bias term, which are zerosend